import React, { useState, useEffect } from 'react';
import './App.css';

export default function App() {
  const [availability, setAvailability] = useState('Availability');
  const [searchText, setSearchText] = useState('');
  const [currentSlide, setCurrentSlide] = useState(0);
  const [profiles, setProfiles] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/users/all') // adjust if your endpoint is different
      .then(res => res.json())
      .then(data => setProfiles(data))
      .catch(err => console.error('Failed to fetch users:', err));
  }, []);

  const filteredProfiles = profiles.filter(profile =>
    profile.name.toLowerCase().includes(searchText.toLowerCase()) ||
    profile.skills.toLowerCase().includes(searchText.toLowerCase())
  );


  const profilesPerSlide = 3;
  
  const totalSlides = Math.ceil(filteredProfiles.length / profilesPerSlide);
  const startIndex = currentSlide * profilesPerSlide;
  const visibleProfiles = filteredProfiles.slice(startIndex, startIndex + profilesPerSlide);


  return (
    <div className="app-container">
      {/* Header */}
      <header className="header">
        <div>Skill Swap</div>
        <div className="log">Login</div>
      </header>

      <hr className="divider" />

      {/* Filters and Search */}
      <div className="filters-search">
        <div>
          <select
            value={availability}
            onChange={e => setAvailability(e.target.value)}
            className="select-availability"
          >
            <option disabled>Availability</option>
            <option>Available</option>
            <option>Busy</option>
          </select>
        </div>
        <div className="search-wrapper"> {/* Wrap search label and input */}
          <label htmlFor="search" className="search-label">Search</label>
          <input
            id="search"
            type="text"
            placeholder="Enter your text"
            value={searchText}
            onChange={e => setSearchText(e.target.value)}
            className="search-input"
          />
        </div>
      </div>

     
      

      <div className="profiles">
        {visibleProfiles.map(profile => (
          <div key={profile.id} className="profile-card">
            <div className="profile-info">
              <div className="profile-avatar"></div>
              <div className="profile-text">
                <div className="profile-name">{profile.name}</div>
                <div className="profile-skills">{profile.skills}</div>
              </div>
            </div>
            <button className="request-button">Request</button>
          </div>
        ))}
      </div>

      {/* Carousel dots */}
      <div className="carousel-dots">
        {[...Array(totalSlides)].map((_, index) => (
          <button
            key={index}
            className={`dot ${index === currentSlide ? 'active' : ''}`}
            onClick={() => setCurrentSlide(index)}
          />
        ))}
      </div>
    </div>
  );
}
